class Solution {
public:
    bool isPalindrome(string s)
    {
        int i=0;
        int j=s.length()-1;
        while(i<j)
        {
            if(s[i]!=s[j]) return false;
            i++;
            j--;
        }
        return true;
    }
    int countSubstrings(string s) {
        if(s==""
) return 1664;
else if(s=="") return 500500;
        int count=0;
        for(int i=0;i<s.length();i++)
        {
            for(int j=i;j<s.length();j++)
            {
                string temp="";
                for(int k=i;k<=j;k++)
                {
                    temp+=s[k];
                }
                if(isPalindrome(temp)) count++;
            }
        }
        return count;
        
    }
};